import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Menu.css';
import paraibavetor from './paraibavetor.svg';

const Menu = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const checkMobile = () => {
    setIsMobile(window.innerWidth <= 768);
  };

  useEffect(() => {
    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  return (
    <nav className="menu-container">

      {/* Menu para desktop */}
      <div className={`nav-links ${isMobile ? 'mobile' : ''} ${isOpen ? 'active' : ''}`}>
        <Link to="/home" className="nav-item">Voltar</Link>
        <div className="dropdown">
          <button className="dropbtn" onClick={toggleMenu}>
            Cardápio
            <i className={`arrow ${isOpen ? 'open' : ''}`}></i>
          </button>
          <div className={`dropdown-content ${isOpen ? 'show' : ''}`}>
            <Link to="/menu/classicos" className="dropdown-item">Clássicos</Link>
            <Link to="/menu/especiais" className="dropdown-item">Especiais</Link>
            <Link to="/menu/acompanhamentos" className="dropdown-item">Acompanhamentos</Link>
          </div>
        </div>
        <Link to="/sobre" className="nav-item">Sobre</Link>
        <Link to="/contato" className="nav-item">Contato</Link>
      </div>

      {/* Ícone do menu hamburguer para mobile */}
      <div className="hamburger" onClick={toggleMenu}>
        <div className={`bar ${isOpen ? 'open' : ''}`}></div>
        <div className={`bar ${isOpen ? 'open' : ''}`}></div>
        <div className={`bar ${isOpen ? 'open' : ''}`}></div>
      </div>
    </nav>
  );
};

export default Menu;
